INSERT INTO PRODOTTO (idProdotto,nome,marca,prezzo,descrizione,img,qnt,tipo) VALUES (idProdotto,'Zero Light Oak','Dometic',200,'Tavolo da campeggio, leggero e pieghevole. Tavolo ripiegabile grande, ultra-leggero. Vanta un nuovo sistema di apertura one-touch, telaio leggero in alluminio e piano in rovere chiaro.','dometic_zero.png',1,'tavolo');
INSERT INTO PRODOTTO (idProdotto,nome,marca,prezzo,descrizione,img,qnt,tipo) VALUES (idProdotto,'ECOVIP CAMPERVAN','Laika',54000,'La maestria artigianale italiana incanta per l’estetica e convince per la tecnica. La serie Ecovip è parte integrante della gamma Laika dal 1992 e continuerà a ispirare per decenni famiglie intere di viaggiatori con un’eccellente tecnologia e un’inimitabile combinazione di bellezza, comfort e spirito d’avventura.','Laika_Ecovip_CamperVan.png',10,'furgonato');
INSERT INTO PRODOTTO (idProdotto,nome,marca,prezzo,descrizione,img,qnt,tipo) VALUES (idProdotto,'KOSMO MANSARDATO','Laika',60000,'Eleganza accogliente e struttura spaziosa. Grazie alla sua zona notte integrata, Kosmo Mansardato offre spazio a sufficienza per tutta la famiglia: per una vacanza nel segno della comodità.','Kosmo-Mansardato.png',10,'mansardato');
INSERT INTO PRODOTTO (idProdotto,nome,marca,prezzo,descrizione,img,qnt,tipo) VALUES (idProdotto,'KREOS PROFILATO','Laika',75000,'La serie Kreos coniuga lusso, tecnologia e versatilità. Un’autentica combinazione di stile, comfort di guida e raffinato design italiano: nel Kreos, l’eleganza non conosce confini e viaggiare in camper diventa un sogno ad occhi aperti.','Laika-Kreos-Low_Profile.png',1,'profilato');
INSERT INTO PRODOTTO (idProdotto,nome,marca,prezzo,descrizione,img,qnt,tipo) VALUES (idProdotto,'ECOVIP MOTORHOME','Laika',95000,'Oltre all’impareggiabile design interno ed esterno, peculiarità della gamma Ecovip Motorhome è il perfetto isolamento, sinonimo di temperature ideali e massimo comfort tutto l’anno.','Ecovip-Motorhome.png',1,'motorhome');
INSERT INTO PRODOTTO (idProdotto,nome,marca,prezzo,descrizione,img,qnt,tipo) VALUES (idProdotto,'BOXDRIVE XL','Knaus',73000,'Un miracolo dal punto di vista tecnologico ed in termini di spazio, pronto ad affrontare qualsiasi avventura, grazie alla sua dinamica di guida su telaio MAN TGE e ad una lunghezza inferiore ai 6 metri. Non importa se siete in viaggio con i familiari o con gli amici, grazie al letto nel tetto rialzato, le due zone notte sono separate per garantire il massimo comfort per 4 persone.','knaus-boxdrive-xl.png',10,'furgonato');
INSERT INTO PRODOTTO (idProdotto,nome,marca,prezzo,descrizione,img,qnt,tipo) VALUES (idProdotto,'L!VE TI','Knaus',64800,'Percepire la libertà, godersi la vita, scoprire il mondo! Con il L!VE TI nuovo di zecca l’approccio al mondo del camping KNAUS diventa così facile come mai prima d’ora. Scegliere una delle 4 piante e le numerose opzioni – e adattare il proprio L!VE TI esclusivamente alle proprie esigenze. Sia per un lungo viaggio o solo per una escursione nel fine settimana: con il L!VE TI si vive il design dinamico, la qualità di prima classe e il rapporto prezzo/prestazioni unico nel suo genere, che crea nuovi standard. ','knaus-liveti.png',10,'profilato');
INSERT INTO PRODOTTO (idProdotto,nome,marca,prezzo,descrizione,img,qnt,tipo) VALUES (idProdotto,'SUN I','Knaus',130000,'Il nostro liner di lusso si presenta in perfetta forma fin nei minimi dettagli. Percorrendo lunghi tratti sulle autostrade del nord o tortuose strade di campagna nei paesi mediterranei, con la sua maschera anteriore e gli esterni dinamici il SUN I colpisce per l’aspetto lussuoso e l’eleganza sportiva. Il SUN I non convince solo per il suo aspetto imponente, ma anche per la particolarità dei suoi dettagli interni, come il doppio pavimento riscaldato in stile yacht o le pregiate finiture in pelle. Salite, e innamoratevi a prima vista.  ','knaus-suni.png',10,'motorhome');
INSERT INTO PRODOTTO (idProdotto,nome,marca,prezzo,descrizione,img,qnt,tipo) VALUES (idProdotto,'Twin Sport','Adria',64000,'I nostri modelli Twin Sports attireranno i più avventurosi, grazie ai posti letto aggiuntivi, al soggiorno e allo spazio di stivaggio. Realizzato sul nuovo Fiat Ducato, con tetto a soffietto dal design esclusivo e vano notte. Perfetto per gli sportivi con attrezzatura outdoor e anche per le famiglie con bambini.','adria_twin.png',10,'furgonato');
INSERT INTO PRODOTTO (idProdotto,nome,marca,prezzo,descrizione,img,qnt,tipo) VALUES (idProdotto,'Coral XL','Adria',75000,'La gamma Coral XL definisce il punto di riferimento per i camper mansardati eleganti e pratici. Linee eleganti e interni spaziosi e pratici fino a 6 posti omologati, che li rendono perfetti per famiglie numerose e gruppi di amici.','adria_coral_xl.png',10,'mansardato');